document.querySelector(".toggleNav").onclick=function(e){e.preventDefault(),document.querySelector("#main-navbar .nav-navbar").classList.toggle("active")};var anchors=document.querySelectorAll('a[href*="#"]');for(var i in anchors)anchors[i].onclick=function(e){e.preventDefault(),target=this.getAttribute("href"),document.querySelector(target).scrollIntoView({behavior:"smooth"})};var btnModal=document.querySelectorAll('[data-action="modal"]');for(var i=0 in btnModal)btnModal[i].onclick=function(e){e.preventDefault();var t=this.getAttribute("data-target");document.getElementById(t).classList.add("active"),console.log(t)};var closeModal=document.querySelectorAll(".modal .modal-bg, .modal .close");for(var i=0 in closeModal)closeModal[i].onclick=function(){this.closest(".modal").classList.remove("active")};